    private void samadenganActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_samadenganActionPerformed
        // TODO add your handling code here:
        switch (operasi) {
  case 1:
      bilangan2 = Double.parseDouble(angka);
      hasil = bilangan1 * bilangan2;
      angka = Double.toString(hasil);
      output.setText(String.format("%.2f", hasil));
      break;
  case 2:
      bilangan2 = Double.parseDouble(angka);
      hasil = bilangan1 / bilangan2;
      angka = Double.toString(hasil);
      output.setText(String.format("%.2f", hasil));
      break;
   case 3:
      bilangan2 = Double.parseDouble(angka);
      hasil = bilangan1 + bilangan2;
      angka = Double.toString(hasil);
      output.setText(String.format("%.2f", hasil));
      break;
   case 4:
      bilangan2 = Double.parseDouble(angka);
      hasil = bilangan1 - bilangan2;
      angka = Double.toString(hasil);
       output.setText(String.format("%.2f", hasil));
      break;
      default:
      break;
 }
    }//GEN-LAST:event_samadenganActionPerformed

    private void hapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hapusActionPerformed
        // TODO add your handling code here:
        output.getText().length();
        StringBuilder all = new StringBuilder (output.getText());
        char end = all.charAt(all.length()-1);
        all.deleteCharAt(all.length()-1);
        output.setText(all.toString());
    }//GEN-LAST:event_hapusActionPerformed

